import React from 'react';
import { useTrail, animated } from 'react-spring'

const Card = (props) => {
    const { image } = props;

    const trail = useTrail(image.length, {
        opacity: 1
    })
    return trail.map( style => (
        <animated.div style={{...style}} className="img-container">            
            <img src={image.url} alt={image.public_id} ></img>
        </animated.div>
    
    ));
};

export default Card;
