import React from 'react';
import { useSpring, animated } from 'react-spring';

const Card = (props) => {
    const { image } = props;

    const enterEffect = useSpring({
        from: { opacity: 0},
        to: { opacity: 1 },
        config: { duration: 2000 },
        delay: 1000
      })

    return (
        <animated.div className="img-container">
            <img src={image.url} alt={image.public_id} style={{...enterEffect}}></img>
        </animated.div>   
    ) 
};

export default Card;
