import { useEffect, useState } from 'react';
import { getImages } from '../api';
import { animated, useSpring, config } from 'react-spring';

const Collection = () => {
    const [imageList, setImageList] = useState([]);
    const [filterValue, setFilterValue] = useState('');
    const checkboxsFilter = [
        {id: 0, label: "Objets"},
        {id: 1, label: "Mariages"},
        {id: 2, label: "Portrait"}, 
        {id: 3, label: "Paysages"}];
    const cardStyle = useSpring({
        from: { opacity: 0},
        to: { opacity: 1 },
        config: { duration : 3000},
        delay: 1500
    })
    useEffect(() => {
        const fetchData = async () => {
            const responseJson = await getImages();
            setImageList(responseJson.resources);
        };
        fetchData();   
    }, [])
    const handleFilterImage = (e) => {
        if(filterValue === '' || e.target.value !== filterValue) {
            setFilterValue(e.target.value);
            e.target.checked =true
        } else {
            setFilterValue('');
            e.target.checked = false
        }
    }
    return (
        <div className="collection">
            <div className="filter">
                {checkboxsFilter.map((checkbox) => {
                    return (
                        <li key={checkbox.id}>
                            <input 
                                type="radio"
                                value={checkbox.label} 
                                id={checkbox.label}
                                checked={checkbox !== '' && checkbox.label === filterValue}
                                onClick={handleFilterImage}
                            />
                            <label htmlFor={checkbox.label}>{checkbox.label}</label>
                        </li>
                    )
                })}
            </div>
            <div className="gallery">
                <div className='image-grid'>
                    {imageList
                        .filter((image) => image.public_id.includes(filterValue)) 
                        .map((image) => (
                            <animated.div style={{...cardStyle}} className="img-container" key={image.asset_id}>              
                                <img src={image.url} alt={image.public_id} ></img>
                            </animated.div>
                    ))}
                </div>
            </div>
        </div>
    )};

export default Collection;
