import React, { useEffect, useState } from 'react';
import { getImages } from '../api';
import { useTrail, animated, Trail } from 'react-spring';

const Collection = () => {
    const [imageList, setImageList] = useState([]);
    const [nextCursor, setNextCursor] = useState(null);
    const [filterValue, setFilterValue] = useState('');
    const checkboxsFilter = [
        {id: 0, label: "Objets"},
        {id: 1, label: "Mariages"},
        {id: 2, label: "Portrait"}, 
        {id: 3, label: "Paysages"}];

    const trail = useTrail(imageList.length, { 
        opacity: 1, 
        from: { opacity: 0 } 
    });
    

    useEffect(() => {
        const fetchData = async () => {
            const responseJson = await getImages();
            setImageList(responseJson.resources);
            setNextCursor(responseJson.next_cursor)
        }
        fetchData();   
    }, [])

    const handleLoadMoreButtonClick= async () => {
        const responseJson = await getImages(nextCursor);
        setImageList((currentImageList) => [
            ...currentImageList, 
            ...responseJson.resources
        ]);
        setNextCursor(responseJson.next_cursor)
    };
    // const handleFilterImages = async () => {
    //     const responseJson = await filteredImages(filterValue, nextCursor);
    //     setImageList(responseJson.resources);
    //     setNextCursor(responseJson.next_cursor)   
    // }
    return (
        <div className="collection">
            <div className="filter">
                {checkboxsFilter.map((checkbox) => {
                    return (
                        <li key={checkbox.id}>
                            <input 
                                type="radio"
                                value={checkbox.label} 
                                id={checkbox.label}
                                checked={checkbox !== '' && checkbox.label === filterValue}
                                onClick={(e) => {
                                    if(filterValue === '' || checkbox.label !== filterValue) {
                                        setFilterValue(e.target.value);
                                        e.target.checked =true
                                    } else {
                                        setFilterValue('');
                                        e.target.checked = false
                                    }
                                
                                    // handleFilterImages()
                                }}
                            />
                            <label htmlFor={checkbox.label}>{checkbox.label}</label>
                        </li>
                    )
                })}
            </div>
            <div className="gallery">
                <div className='image-grid'>
                    {imageList
                    .filter((image) => image.public_id.includes(filterValue)) 
                    .map((image, props) => (
                        <Trail
                            >
                                
                        <animated.div style={{...props}} className="img-container" key={image.asset_id}>              
                            <img src={image.url} alt={image.public_id} ></img>
                        </animated.div>
                        </Trail>
                    ))}
                </div>
                {nextCursor && <button onClick={handleLoadMoreButtonClick}>Voir plus</button>}     
            </div>
        </div>
    )};

export default Collection;
