import React from 'react';
import { useTrail, animated } from 'react-spring'

const Card = (props) => {
    const { image } = props;

    const trail = useTrail(image.length, {
        opacity: 1
    })
    return (
    trail.map( style => (
        <div style={{...style}} className="img-container">            
            <animated.img src={image.url} alt={image.public_id} ></animated.img>
        </div>
    
    )));
};

export default Card;
