import React from 'react';
import { animated, useTrail } from 'react-spring';

const Card = (props) => {
    const { image } = props;

    const trail = useTrail(10, {
        config: {duration:1000},
        opacity: 1,
        transform: 'scale(1)',
        from: { transform: 'scale(0)' }
      });
    return (     
        <animated.div className="img-container">
                    <img  src={image.url} alt={image.public_id} style = {{...style}}></img>
        </animated.div>
        )
    
};

export default Card;
