import React from 'react';
import { useTransition, animated } from '@react-spring/core';


const HomeTitle = () => {
    const transitions = useTransition({
        from: { opacity: 0},
        enter: { opacity: 1 },
        delay: 1000
      })
    return transitions((props) =>
        <animated.div 
            className="title"     
            style= {{...props}}
        >
            <h1>Photograph's name</h1>
            <h3>Votre histoire</h3>
        </animated.div>
    );
};

export default HomeTitle;
