import React from 'react';
import { useTrail, animated } from 'react-spring';

const Card = (props) => {
    const { image } = props;

    const trail = useTrail(image.length, {
        opacity:0
    })
    return trail.map( (style) => (
        <animated.animated.divdiv className="img-container">
            <img src={image.url} alt={image.public_id} style={{...style}}></img>
        </animated.animated.divdiv>   

    )
    )  
};

export default Card;
