import React, { useState, useEffect } from 'react'
import { useTransition, animated } from '@react-spring/web'
import styles from '../styles/components/_home.scss'

const slides = [
  'https://unsplash.com/photos/l0JqBuPN37s',
  'https://unsplash.com/photos/0jWMq0eukJ8',
  '9dxDrr7GvEI',
  '8mJmtSSOXO4',
]

export default function App() {
  const [index, set] = useState(0)
  const transitions = useTransition(index, {
    key: index,
    from: { opacity: 0 },
    enter: { opacity: 1 },
    leave: { opacity: 0 },
    config: { duration: 1000 },
  })
  useEffect(() => {
    const t = setInterval(() => set(state => (state + 1) % slides.length), 4000)
    return () => clearTimeout(t)
  }, [])
  return (
    <div className="flex fill center">
      {transitions((style, i) => (
        <animated.div
          className={styles.bg}
          style={{
            ...style,
            backgroundImage: `url(https://unsplash.com/photos/${slides[i]})`,
          }}
        />
      ))}
    </div>
  )
}
