import React from 'react';

const Card = (props) => {
    const { image } = props;
    const trail = useTrail(1, {
        config: {duration:1000},
        opacity: 1,
        transform: 'scale(1)',
        from: { transform: 'scale(0)' }
      });
    return (
        <div className="img-container">            
                <img  src={image.url} alt={image.public_id} ></img>
        </div>
        )
    
};

export default Card;
