import React from 'react';
import { useTransition } from 'react-spring';

const Card = (props) => {
    const { image } = props;

    const transitions = useTransition()

    return transitions((style) => (
        <div className="img-container">
            <img src={image.url} alt={image.public_id} style={{...style}}></img>
        </div>
    )     
    )  
};

export default Card;
